import React, { useState, useEffect } from "react";
import { useGeolocated } from "react-geolocated";
import { BrowserRouter as Router, Routes, Route, Link } from "react-router-dom";
import Navbar from "./components/Navbar/Navbar";
import SmsPage from "./components/SmsPage/SmsPage"; // Import the SMS page component
import "./App.css";

const App = () => {
  const [longitude, setLongitude] = useState(null);
  const [latitude, setLatitude] = useState(null);
  const [danger, setDanger] = useState(null);
  const [showApp, setShowApp] = useState(false);

  const { coords } = useGeolocated();

  useEffect(() => {
    if (coords) {
      setLongitude(coords.longitude);
      setLatitude(coords.latitude);
    }
  }, [coords]);

  useEffect(() => {
    const timeout = setTimeout(() => {
      setShowApp(true);
    }, 3000);

    return () => clearTimeout(timeout);
  }, []);

  const handlePredict = async () => {
    try {
      const response = await fetch("http://localhost:5000/predict", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          longitude: longitude,
          latitude: latitude,
        }),
      });
      const data = await response.json();
      setDanger(data.message);
    } catch (error) {
      console.error("Error predicting danger:", error);
    }
  };

  return (
    <Router>
      <div>
        {!showApp && (
          <div className="welcome-container">
            <h1 className="welcome-text">Welcome!</h1>
            <p className="additional-text">Stay safe on the go with Safe Walk AI!</p>
          </div>
        )}
        <div className={`App ${showApp ? "show" : ""}`}>
          {showApp && (
            <>
              <Navbar />
              <div className="description">
                <p>
                  Safe Walk AI! Predict potential dangers or text for personalized safety tips with just a tap.
                </p>
              </div>
              {longitude && latitude && (
                <div>
                  Latitude: {latitude}, Longitude: {longitude}
                </div>
              )}
              <Link to="/SmsPage" className="text-button">
                SMS
              </Link>
              <button className="predict-button" onClick={handlePredict}>
                Safety
              </button>
              {danger !== null && <div>{danger}</div>}
            </>
          )}
        </div>
      </div>
      <Routes>
        <Route path="/SmsPage" element={<SmsPage />} />
      </Routes>
    </Router>
  );
};

export default App;
